import React from "react";
import { useState, useEffect } from'react'
import { useDispatch, useSelector } from 'react-redux'
import { getDogs, getDogsByName, getTemperaments, getDogsById, postDog, filterByCreation } from "../redux/actions";
import { Link } from 'react-router-dom'
import Card from './Card'
import Paginado from "./Paginado";


export default function Home(){
    
    let prevId = 1    
    const dispatch = useDispatch()
    const allDogs = useSelector((state) => state.dogs)
    console.log(allDogs)

const [ loading, setLoading] = useState(false)

const [order, setOrder] = useState('')
const [currentPage, setCurrentPage] = useState(1)
const [dogsPerPage, setDogsPerParge] = useState(8)
const indexOfLast = currentPage * dogsPerPage
const indexOfFirst = indexOfLast - dogsPerPage
const [currentDogs, setCurrentDogs] = useState([])

const pagination = (pageNumber) => {
    setCurrentPage(pageNumber)
    setCurrentDogs(allDogs?.slice(indexOfFirst, indexOfLast))
}


useEffect(()=>{
    setLoading(true)
    setTimeout(() => {
    setLoading(false)
    },2000)
dispatch(getDogs())
},[dispatch])

useEffect(() => {
    if(allDogs){
        setCurrentDogs(allDogs?.slice(indexOfFirst, indexOfLast))
    }
},[allDogs])




function handleFilterChange(e){
    e.preventDefault();
    dispatch(filterByCreation(e.target.value))
    setCurrentPage(1)
    setOrder(`Order ${e.target.value}`)
}

if(loading){
    return(
        <div>Loading...</div>
    )
} else
return (
    <div>
        <div>
        <h1>Dogs</h1>
        </div>
        <div>
            <Link to='/form'>
                <button>Mix your DNA to create a new dog</button>
            </Link>
        </div>
        <div>
            <select defaultValue="Existence" name="existence filter" onChange={e => handleFilterChange(e)}>
                <option value='all' >All</option>
                <option value='byApi'>old breed</option>
                <option value='byDb'>new breed</option>
            </select>
        </div> 
        <div>
            {currentDogs && currentDogs.map(e => {
                return(
                    <div key={prevId++} >
                        <Link to={`/${e.id}`} >
                            <Card
                            image = {e.image ? e.image : e.img}
                            name = {e.name}
                            temperaments = {e.temperaments}
                            key = {e.id}
                            />
                        </Link>
                    </div>
                )
            })}
        </div>

        
            {
                <Paginado
                dogsPerPage = {dogsPerPage}
                allDogs = {allDogs.length}
                paginado = {pagination}
            />}
        
    </div>
)

};
