import React from "react"
import { GET_DOGS, GET_DOGS_BY_NAME, GET_TEMPERAMENTS, GET_DOGS_BY_ID, POST_DOG, FILTER_BY_CREATION, ALPHABETICAL_SORT } from "./actions"

const initialState = {
    dogs: [],
    allDogs: [],
    temperaments: [],
    detail: [] 
}



export default function rootReducer(state = initialState, action){
    switch(action.type){
        case GET_DOGS: 
            return {
                ...state,
                dogs: action.payload,
                allDogs: action.payload
            };
        case GET_DOGS_BY_NAME:
            return{
                ...state,
                dogs: action.payload
            };
        case GET_TEMPERAMENTS:
            return{
                ...state,
                temperaments: action.payload
            };
        case GET_DOGS_BY_ID:
            return{
                ...state,
                dogs: action.paylaod
            };
        case POST_DOG:
            return{
                ...state,
                dogs: action.payload
            };
        case FILTER_BY_CREATION:
            const allDogs = [...state.dogs]
            //const dbDogs  = allDogs.filter(el => el.id.includes('-'))
            const filterCreation = action.payload === 'all' ? allDogs : 
            allDogs.id.includes('-') ?
              action.payload === 'created'
              :
              action.payload === 'api'
              
            return{
                ...state,
                dogs: filterCreation
            };
        case ALPHABETICAL_SORT:
            const infoDogs = [...state.dogs]
            const alphaSorted = action.payload === 'asc' ?
            infoDogs.sort(function (a, b) {
                if(a.name > b.name) return 1
                if(a.name < b.name) return -1
                return 0;
            }) :
            infoDogs.sort(function (a, b) {
                if(a.name < b.name) return 1
                if(a.name > b.name) return -1
                return 0;
            });  
            return{
                ...state,
                dogs : alphaSorted
            }  

        default:
            return state


    }}
