import React from "react";
import { Link, useParams } from 'react-router-dom'
import { useState, useEffect } from 'react'
import { useDispatch, useSelector } from "react-redux";
import { getDogsById, getTemperaments } from "../redux/actions";
import './Detail.css'
import axios from 'axios'


export default function Detail(){
    const dispatch = useDispatch();
    const {id} = useParams();
    const [loading, setLoading] = useState(false)

    const detail = useSelector((state) => state.detail)

    console.log(detail)

    useEffect(() => {
        dispatch(getDogsById(id))
    }, [dispatch, id])


    useEffect(() => {
        setLoading(true)
        setTimeout(() => {
            setLoading(false)
        }, 2000)
    }, [dispatch, id])


    
    
    
    
    
    if(loading){
        return (
            <div className="LoaderContainerId">
                <div className="LoadingId">Loading...</div>
                <div className="spinnerId"></div>
            </div>
        )
    }else{
        
        var info = async () => await  axios.get('http://localhost:3001/dogs/' + id)
        console.log(info.data)
           info.data?.map( (el) =>{  return(
              
                <div>
                    <h2>name: {el.name} </h2>
                </div>,
                <div>
                    <img src={el.image} alt="" />
                </div>
               )  }
              

           )
        
             
         
            }



}
