 import React from "react";
// import axios from 'axios'
import { Link, useParams } from 'react-router-dom'
import { useState, useEffect } from 'react'
import { useSelector, useDispatch } from "react-redux";
import { getDogsById, getTemperaments } from "../redux/actions";
//import allReducers from "../redux";
//import  rootReducer from '../redux/reducer' 
import './Detail.css'



 
export default function Detail(){
    const dispatch = useDispatch();
    const detail = useSelector((state) => state.detail)
    const {id} = useParams();
    const [loading, setLoading] = useState(false)

    console.log(detail)

    const dogg = useSelector((state) => state.dogs)
    console.log(dogg)


    console.log(useSelector(state => state))


    
    
    useEffect(() => {
      //  dispatch(getTemperaments(id))
        dispatch(getDogsById(id))
    }, [dispatch, id])
    
    
    useEffect(() => {
        setLoading(true)
        setTimeout(() => {
            setLoading(false)
        }, 3000)
    }, [dispatch, id])
    
    
    
    
    
    var matchId =  dogg.map(el => el.id === id ? el : "not match found")
    console.log(matchId)
    
    if(loading){
        return (
            <div className="LoaderContainerId">
                <div className="LoadingId">Loading...</div>
                <div className="spinnerId"></div>
            </div>
        )
    }else{
        return (
            <div>
            {  dogg?.map(el => el.id === id  ?
            <h1 key={id}> NOMBRE: { el.name} </h1>
            :
            "dog not found")}
            </div>
            
                
               )
               
         
            }

            
        }
