import React from 'react';
import axios from 'axios'

export const GET_DOGS = 'GET_DOGS'
export const GET_DOGS_BY_NAME = 'GET_DOGS_BY_NAME'
export const GET_TEMPERAMENTS = 'GET_TEMPERAMENTS'
export const GET_DOGS_BY_ID = 'GET_DOGS_BY_ID'
export const POST_DOG = 'POST_DOG'
export const FILTER_BY_CREATION = 'FILTER_BY_CREATION'






export function getDogs(){
return async function(dispatch){
    try{
        const json = await axios.get('http://localhost:3001/dogs');
        return dispatch({
            type: GET_DOGS,
            dogs: json.data,
            allDogs: json.data
        })
        console.log(json)
    }catch(error){
        console.log(error)
    }
}
};

export function getDogsByName(name){
    return async function(dispatch){
        try{
           let response = await axios.get(`http://localhost:3001/dogs?name=${name}`)
           return dispatch({
            type: GET_DOGS_BY_NAME,
            payload: response.data
           })
        }catch(error){
            console.log(error)
        }
    }
};

export function getTemperaments(){
    return async function(dispatch){
        try{
            let temps = await axios.get('http://localhost:3001/temperament');
            return dispatch({
                type: GET_TEMPERAMENTS,
                temperaments: temps.data
            })
        }catch(error){
            console.log(error)
        }
    }
};

export function getDogsById(id){
    return async function(dispatch){
        try{
            let info = await axios.get('http://localhost:3001/dogs')
            let matchId = await info.data.filter((el) => el.id === id)
            return dispatch({
                type: GET_DOGS_BY_ID,
                payload: matchId
            })

        }catch(error){
            console.log(error)
        }
    }
};

export function postDog(){
    return async function(dispatch){
        try{
           let dogPosted = await axios.post('http://localhost:3001/form')
           return dispatch({
            type: POST_DOG,
            payload: dogPosted
           })
        }catch(error){
            console.log(error)
        }
    }
};

export function filterByCreation(payload){
  
    try{
        return ({
          type: FILTER_BY_CREATION,
          payload       
        })
    }catch(error){
        console.log(error)
    }
  
}
