import React from 'react'
import { Link, useParams } from 'react-router-dom'
import { useState, useEffect } from 'react'
import { useDispatch, useSelector } from 'react-redux'
import { getDogsById } from '../redux/actions'
import './Detail.css'


export default function Detail(props){
const dispatch = useDispatch();
const {id} = useParams()
const [loading, setLoading] = useState(false)
const detail = useSelector(state => state.details)


useEffect( () => {
    setLoading(true)
    setTimeout(() => {
        setLoading(false)
    }, 2000)
    dispatch(getDogsById(id))
}, [dispatch, id])





if(loading){
    return(
        <div>Loading... =P</div>
    )
} else {

} return (
    <div className="details" key= {id}>
        <div className="divimg">
          <img className="detailImg" src= {props?.detail && detail?.image } alt=""/>  
        </div>
        <h1 className='texts' >{ detail?.name || detail[0].name  }</h1>

        <div> 
            <h3>Temperaments:</h3>
        {
            detail?.temperaments ? detail.temperaments?.map((e, index) => {
                return(
                    <h5 key={index} >{e}</h5>
                )
            }) :
            detail?.temperaments.map((e, index) => {
                return (
                    <h3 key={index} > {e.name} </h3>
                )
            })
        } 
        </div>

        <div>
            
        </div>


       <Link to="/home" ><button className="backButton">Back</button></Link>
    </div>
)

};
