      temperament: detail.temperaments,
      weight: detail.weight,
      height: detail.height,
      life_span: detail.life_span,
      image: detail.image
    })
}, [dispatch, id])
    
useEffect( () => {
    setLoading(true)
    setTimeout(() => {
        setLoading(false)
    }, 2000)
    
}, [dispatch, id])
 







