import React from "react"
import { GET_DOGS, GET_DOGS_BY_NAME, GET_TEMPERAMENTS, GET_DOGS_BY_ID, POST_DOG, FILTER_BY_CREATION, FILTER_BY_TEMPERAMENTS, ALPHABETICAL_SORT, WEIGHT_SORT } from "./actions"

const initialState = {
    dogs: [],
    allDogs: [],
    temperament: [],
    detail: [] 
}



export default function rootReducer(state = initialState, action){
    switch(action.type){
        case GET_DOGS: 
            return {
                ...state,
                dogs: action.payload,
                allDogs: action.payload
            };
        case GET_DOGS_BY_NAME:
            return{
                ...state,
                dogs: action.payload
            };
        case GET_TEMPERAMENTS:
            return{
                ...state,
                temperament: action.payload
            };
        case GET_DOGS_BY_ID:
            return{
                ...state,
                detail: action.paylaod
            };
        case POST_DOG:
            return{
                ...state,
                dogs: action.payload
            };
        case FILTER_BY_CREATION:
            const allDogs = state.allDogs
            const filterCreation = action.payload === 'created' ?
            allDogs.filter(e => e.id.toString().includes('-')) :
            allDogs.filter(e => !e.id.toString().includes('-'))             
            return{
                ...state,
                dogs: action.payload === 'All' ? allDogs : filterCreation
            };
        
        case FILTER_BY_TEMPERAMENTS:
            const allD = state.dogs
            let filteredDogs = [];
            if(action.payload === 'All'){
                filteredDogs = allD
            }else{
                for(let i = 0; i < allD.length; i++){
                    let match = allD[i].temperaments.find((t) => t === action.payload);
                    if(match){
                        filteredDogs.push(allD[i]);
                    }
                }
            } 
                return{
                ...state,
                dogs: filteredDogs
            };  
            
        case ALPHABETICAL_SORT:
            const infoDogs = [...state.dogs]
            const alphaSorted = action.payload === 'asc' ?
            infoDogs.sort(function (a, b) {
                if(a.name > b.name) return 1
                if(a.name < b.name) return -1
                return 0;
            }) :
            infoDogs.sort(function (a, b) {
                if(a.name < b.name) return 1
                if(a.name > b.name) return -1
                return 0;
            });  
            return{
                ...state,
                dogs : alphaSorted
            };
        
        case WEIGHT_SORT:
            const doggs = [...state.dogs]
            const weightSorted = action.payload === 'fromMin' ?
            doggs.sort(function (a, b) {
                if(a.weight[0] > b.weight[0]) return 1
                if(a.weight[0] < b.weight[0]) return -1
                return 0;
            }) :
            doggs.sort(function (a, b) {
                if(a.weight[0] < b.weight[0]) return 1
                if(a.weight[0] > b.weight[0]) return -1
                return 0;
            });  
            return{
                ...state,
                dogs : weightSorted
            };
        

        default:
            return state


    }}
