import React from 'react'
import { Link, useParams } from 'react-router-dom'
import { useState, useEffect } from 'react'
import { useDispatch, useSelector } from 'react-redux'
import { getDogsById } from '../redux/actions'
import './Detail.css'


export default function Detail(props){
const dispatch = useDispatch();
const {id} = useParams()
const [loading, setLoading] = useState(false)
const detail = useSelector(state => state.details)


useEffect( () => {
    setLoading(true)
    setTimeout(() => {
        setLoading(false)
    }, 2000)
    dispatch(getDogsById(id))
}, [dispatch, id])


let nameDog, imageDog, temperamentDog = []


if(detail[0]){
    nameDog = detail[0].name
    imageDog = detail[0].image
}
if (detail[0].temperaments[0]) {
    temperamentDog = [...detail[0].temperaments]
}

if (detail[0].temperaments[0].name) {
    temperamentDog = detail[0].temperaments.map(temp => temp.name)
}



if(loading){
    return(
        <div>Loading... =P</div>
    )
} else {

} return (
    <div className="details" key= {id}>
        <div className="divimg">
          <img className="detailImg" src= { imageDog } alt=""/>  
        </div>
        <h1 className='texts' >{ nameDog  }</h1>

        <div> 
            <h3>Temperaments:</h3>
         <ul> 
        {temperamentDog.map(t => <li key={t} >{t}</li>)} 
        </ul>
        </div>

        <div>
            
        </div>


       <Link to="/home" ><button className="backButton">Back</button></Link>
    </div>
)

};
