import React from "react";
import { useState, useEffect } from'react'
import { useDispatch, useSelector } from 'react-redux'
import { getDogs, filterByCreation } from "../redux/actions";
import { Link } from 'react-router-dom'


export default function Home(){

const dispatch = useDispatch()
const allDogs = useSelector(state => state.allDogs)

const [order, setOrder] = useState('')
const [currentPage, setCurrentPage] = useState(1)
const [dogsPerPage, setDogsPerParge] = useState(8)


useEffect(()=>{
dispatch(getDogs)
},[dispatch])



function handleFilterChange(e){
    e.preventDefault();
    dispatch(filterByCreation(e.target.value))
    setCurrentPage(1)
    setOrder(`Order ${e.target.value}`)
}


return (
    <div>
        <div>
        <h1>Dogs</h1>
        </div>
        <div>
            <Link to='/form'>
                <button>Mix your DNA to create a new dog</button>
            </Link>
        </div>
        <div>
            <select defaultValue="Existence" name="existence filter" onChange={e => handleFilterChange(e)}>
                <option value='all' >All</option>
                <option value='byApi'>old breed</option>
                <option value='byDb'>new breed</option>
            </select>
        </div> 
    </div>
)

};
