import React from "react";
import { useState, useEffect } from'react'
import { useDispatch, useSelector } from 'react-redux'
import { getDogs, getDogsByName, getTemperaments, getDogsById, postDog, filterByCreation, filterByTemperaments, alphabeticalSort, weightSort } from "../redux/actions";
import { Link } from 'react-router-dom'
import Card from './Card'
import Paginado from "./Paginado";
import SearchBar from './SearchBar'

let prevId = 1    

export default function Home(){
    
    const dispatch = useDispatch()
    const allDogs = useSelector((state) => state.dogs)
    
    const allTemperaments = useSelector((state) => state.temperament)
    console.log(allTemperaments)
    
const [ loading, setLoading] = useState(false)

const [order, setOrder] = useState('')
const [currentPage, setCurrentPage] = useState(1)
const [dogsPerPage, setDogsPerParge] = useState(8)
const indexOfLast = currentPage * dogsPerPage
const indexOfFirst = indexOfLast - dogsPerPage
const [currentDogs, setCurrentDogs] = useState([])

const pagination = (pageNumber) => {
    setCurrentPage(pageNumber)
    setCurrentDogs(allDogs?.slice(indexOfFirst, indexOfLast))
}


useEffect(()=>{
    setLoading(true)
    setTimeout(() => {
    setLoading(false)
    },2000)
dispatch(getDogs())
},[dispatch])

useEffect(() => {
    if(allDogs){
        setCurrentDogs(allDogs?.slice(indexOfFirst, indexOfLast))
    }
},[allDogs])




function handleFilterChange(e){
    e.preventDefault();
    dispatch(filterByCreation(e.target.value))
    setCurrentPage(1)
    setOrder(`Order ${e.target.value}`)
};

function handleFilterTemp(e){
    e.preventDefault();
    dispatch(filterByTemperaments(e.target.value))
    setCurrentPage(1)
    setOrder(`Order${e.target.value}`)
}



function handleSort(e){
    e.preventDefault();
    dispatch(alphabeticalSort(e.target.value))
    setCurrentPage(1);
    setOrder(`Order ${e.target.value}`)
};


function handleWeightSort(e){
    e.preventDefault();
    dispatch(weightSort(e.target.value))
    setCurrentPage(1);
    setOrder(`Order ${e.target.value}`)
}



 function getTemperaments(){
    return async function(dispatch){
        try{
            const temps = await axios.get('http://localhost:3001/temperament');
            const resp = await temps.data[0]
            console.log(resp)
            return dispatch({
                type: GET_TEMPERAMENTS,
                payload: temps.data,
                temperament: temps.data
            });
        }catch(error){
            console.log(error)
        }
    }
};






 




if(loading){
    return(
        <div>Loading...</div>
    )
} else
return (
    <div>
        <div>
        <h1>Dogs</h1>
        </div>
        <div>
            <SearchBar/>
        </div>
        <div>
            <Link to='/form'>
                <button>create a new dog</button>
            </Link>
        </div>
        <div>
            <select defaultValue="Existence" name="existence" onChange={e => handleFilterChange(e)}>
                <option value='All' >All</option>
                <option value='api'>old breed</option>
                <option value='created'>new breed</option>
            </select>
        </div> 
        <div>
            <select defaultValue='Temperaments' name="temperaments" onChange={e => handleFilterTemp(e)} >
                {/* <option disabled  >Temperaments</option> */}
                <option value = 'All' >All</option>
                    { // * * * *  CONSULTAR CON VIC * * * * * 
                      allTemperaments?.map(el =>{ 
                       return(
                            <option value={el.name} key={el.id}> {el.name} </option>
                       )}
                      )
                    }

     {/* 
              <select onChange={handleFilterByTemperament}>
                  <option disabled selected defaultValue>Temperaments</option>
                  <option value="Todos">All</option>
                  {
                    allTemperaments?.map(temp => (
                        <option value={temp.name}  key={temp.id}>{temp.name}</option>
                    ))
                  }
              </select>

     */}

                
            </select>
        </div>
        <div>
            <select defaultValue='Alphabetical Sort' name="alphabetical" onChange={e => handleSort(e)}>
                <option value='Alphabetical Sort' disabled>Alphabetical Sort</option>
                <option value='asc'  >Ascendent</option>
                <option value='desc' >Descendent</option>
            </select>
        </div>
        <div>
            <select defaultValue='Weight Sort' name="weight" onChange={e => handleWeightSort(e)}>
                <option value='Weight Sort' disabled>Weight Sort</option>
                <option value='fromMin'  >from Min to Max</option>
                <option value='fromMax' >from Max to Min</option>
            </select>
        </div>         
        <div>
            {currentDogs && currentDogs.map(e => {
                return(
                    <div key={prevId++} >
                        <Link to={`/${e.id}`} >
                            <Card
                            image = {e.image ? e.image : e.img}
                            name = {e.name}
                            weight_min = {e.weight[0]}
                            weight_max = {e.weight[1]}
                            temperaments = {e.temperaments}
                            key = {e.id}
                            />
                        </Link>
                    </div>
                )
            })}
        </div>

        
            {
                <Paginado
                dogsPerPage = {dogsPerPage}
                allDogs = {allDogs?.length}
                paginado = {pagination}
            />}
        
    </div>
)

};
