import React from "react";
import {useState} from 'react';
import {useDispatch , useSelector } from 'react-redux';
import{ getDogsByName, getDogs } from '../redux/actions';
import { Link } from "react-router-dom";
import './SearchBar.css'

export default function SearchBar(){
    const dispatch = useDispatch();
    const dog = useSelector(state => state.allDogs)
    const [name, setName] = useState("")
    const [search, setSearch] = useState(false)
    const [currentPage, setCurrentPage] = useState(1)
    const [order, setOrder] = useState('')

  function handleInputChange(e){
    e.preventDefault()
    setName(e.target.value)
    
  } 

  function handleSubmit(e){
    e.preventDefault();
    setSearch(true)
    dispatch(getDogsByName(name))
    setName(name)
   /*  if(name){
      setSearch(true) 
      
    }else{
      setName("")
      setCurrentPage(1)
      setOrder(`Order ${e.target.value}`)
      setSearch(false)
      return alert("Search breed that exist")
    }
    if(document.getElementById("input1").value===""){
      setSearch(false)
     
      return alert("Please choose an existent breed")
    } */
    
  }
  
  function handleBack(e){
    e.preventDefault();
    dispatch(getDogs())
    document.getElementById("input1").value="";
   
  }


    return(
       <div>
        <input 
        name = 'input'
        type = 'text'
        placeholder = 'Search By Breed...'
        onChange = {(e) => handleInputChange(e)}
        id='input1' 
        />
        <button className="btn" onClick={(e) => handleSubmit(e)} type = 'submit' >Search</button>
       
      
        <Link to='/home'>
        <button  onClick={(e) => handleBack(e)}  className="BackBtnSearch">Back</button>
        </Link>
     
       </div>
    )
}
