import React from "react"
import { GET_DOGS, GET_DOGS_BY_NAME } from "./actions"

const initialState = {
    dogs: [],
    allDogs: []
}



export default function rootReducer(state = initialState, action){
    switch(action.type){
        case GET_DOGS: 
            return {
                ...state,
                dogs: action.payload,
                allDogs: action.payload
            };
        case GET_DOGS_BY_NAME:
            return{
                ...state,
                result: action.payload
            };


        default:
            return state


    }}
