import React from 'react'
//import axios from 'axios'
import { Link, useParams } from 'react-router-dom'
import { useState, useEffect } from 'react'
import { useDispatch, useSelector } from 'react-redux'
import { getDogsById, getTemperaments } from '../redux/actions'
import './Detail.css'


export default function Detail(){
const dispatch = useDispatch();
const id = useParams().id
const [loading, setLoading] = useState(false)
const detail = useSelector((state) => state.detail)




useEffect(() => {
  dispatch(getDogsById(id))
  
}, [dispatch, id])
    
useEffect( () => {
    setLoading(true)
    setTimeout(() => {
        setLoading(false)
    }, 2000)
    
}, [dispatch, id])
 







if(loading){
    return(
        <div className="LoaderContainerId">
        <div className="LoadingId">Loading...</div>
        <div className="spinnerId"></div>
        </div>
        )
    } else {
        
    } return (
        <div>
        <div className="details" key= {detail?.id}>
              
    <div  className="cardContainer">
    <div className="card">
    <div >
        <img className="cardImage" src={detail?.image} alt='not found'  />
    </div>
    <div>
        <h2 className="cardTitle">name: {detail?.name || detail[0].name}</h2>
    </div>
     <div className="heightContainer">
        <h6 className="cardName" >weight_min: {detail?.weight_min} kg</h6>
        <h6 className="cardName" >weight_max: {detail?.weight_max} kg</h6>
    </div>
    <div className="heightContainer">
        <h6 className="cardName" >height_min: {detail?.height_min} kg</h6>
        <h6 className="cardName" >height_max: {detail?.height_max} kg</h6>
    </div>
    <div>
        <h5>life_span: {detail?.life_span}</h5>
    </div>
    <div className="parraf">
        {detail?.temperaments && detail?.temperaments?.map(el =>{
            return (
                <h6 className="parraf" key = {el.id}>temperaments: {el.name? el.name : el + "  |  "}</h6>
            )
        })}
    </div>
    </div>
    </div>

            {/*  <div className="divimg">
          <img  src= {  detail?.image  } alt=""/>  
        </div> */}
        {/* <div>
         <h1> Name: {detail?.map(el => el === el.name)}</h1> 
         </div>  */}
         
        {/* <div> 
            <h3>Temperaments:</h3>
        {
            detail?.temperaments ? detail.temperaments?.map((e, index) => {
                return(
                    <h5 key={index} >{e}</h5>
                )
            }) :
            detail?.temperaments.map((e, index) => {
                return (
                    <h3 key={index} > {e.name} </h3>
                )
            })
        } 
        </div> */}
{/* 
        <div>
            
        </div> */}


       { <Link to="/home" ><button className="backButton">Back</button></Link> }
    </div>
    </div>
)

};
