import React from 'react'
//import axios from 'axios'
import { Link, useParams } from 'react-router-dom'
import { useState, useEffect } from 'react'
import { useDispatch, useSelector } from 'react-redux'
import { getDogsById, getTemperaments } from '../redux/actions'
import './Detail.css'


export default function Detail(){
const dispatch = useDispatch();
const id = useParams().id
const [loading, setLoading] = useState(false)
const detail = useSelector((state) => state.detail)
const [dogDetail, setDogDetail] = useState({id:"", name:"", temperament:"", weight:"", height:"", life_span:"", image:""})

//let info = await axios.get('http://localhost:3001/dogs/' + id)


useEffect(() => {
  dispatch(getDogsById(id))
  setDogDetail({
      id: detail.id,
      name: detail.name,
      temperament: detail.temperaments,
      weight: detail.weight,
      height: detail.height,
      life_span: detail.life_span,
      image: detail.image
    })
}, [dispatch, id])
    
useEffect( () => {
    setLoading(true)
    setTimeout(() => {
        setLoading(false)
    }, 2000)
    
}, [dispatch, id])
 







if(loading){
    return(
        <div>Loading... =P</div>
        )
    } else {
        
    } return (
        <div className= "fondoDetail">
            <div className= "containerId">
                <div className= "container-img">
                    <img className= "img-perro" alt="" src= {dogDetail.image}></img>
                </div>
                <div className="descripcion-perro">
                    <p>{dogDetail.name}</p>
                    <div>
                        <p>Temperamento: {dogDetail.temperament}</p>
                        <p>Height:{dogDetail.height}</p>
                        <p>Weight: {dogDetail.weight}</p>
                        <p>Life Span: {dogDetail.life_span}</p>
                    </div>
                </div>
            </div>
        </div>
)

};
