import React from "react"
import { GET_DOGS, GET_DOGS_BY_NAME, GET_TEMPERAMENTS, GET_DOGS_BY_ID, POST_DOG, FILTER_BY_CREATION } from "./actions"

const initialState = {
    dogs: [],
    allDogs: [],
    temperaments: [],
    detail: [] 
}



export default function rootReducer(state = initialState, action){
    switch(action.type){
        case GET_DOGS: 
            return {
                ...state,
                dogs: action.payload,
                allDogs: action.payload
            };
        case GET_DOGS_BY_NAME:
            return{
                ...state,
                result: action.payload
            };
        case GET_TEMPERAMENTS:
            return{
                ...state,
                temperaments: action.payload
            };
        case GET_DOGS_BY_ID:
            return{
                ...state,
                result: action.paylaod
            };
        case POST_DOG:
            return{
                ...state,
                post: action.payload
            };
        case FILTER_BY_CREATION:
            const allDogs = state.dogs
            const filterCreation = action.payload === 'all' ? allDogs : allDogs.filter(el => el.target.value === action.payload) // => CHEQUEAR!!   
            return{
                ...state,
                dogs: filterCreation
            } 

        default:
            return state


    }}
