import React from 'react'
import axios from 'axios'
import {  useParams } from 'react-router-dom'
import { useState, useEffect } from 'react'
import { useDispatch, useSelector } from 'react-redux'
import { getDogsById, getTemperaments } from '../redux/actions'
import './Detail.css'


export default function Detail(){
const dispatch = useDispatch();
const id = useParams().id
const [loading, setLoading] = useState(false)
const detail = useSelector((state) => state.detail)
const [dogDetail, setDogDetail] = useState({id:"", name:"", temperament:"", weight:"", height:"", life_span:"", image:""})

//let info = await axios.get('http://localhost:3001/dogs/' + id)


useEffect(() => {
  dispatch(getDogsById(id))
}, [dispatch, id])

useEffect(() => {
    details()
}, [])

useEffect( () => {
    setLoading(true)
    setTimeout(() => {
        setLoading(false)
    }, 2000)
    
}, [dispatch, id])


const details = () => {
    axios.get(`http://localhost:3001/dogs/${id}`) 
        .then((res) => {
            console.log(res)
            setDogDetail({
                id: res.data.id,
                name: res.data.name,
                temperament: res.data.temperaments,
                weight: res.data.weight?.metric,
                height: res.data.height?.metric,
                life_span: res.data.life_span,
                image: res.data.image?.url
              })
            
          
        })
        .catch((error) => {
            console.log(error);
        }); 
} 





if(loading){
    return(
        <div>Loading... =P</div>
        )
    } else {
        
    } return (
        <div className= "fondoDetail">
            <div className= "containerId">
                <div className= "container-img">
                    <img className= "img-perro" alt="" src= {dogDetail.image}></img>
                </div>
                <div className="descripcion-perro">
                    <p>{dogDetail.name}</p>
                    <div>
                        <p>Temperamento: {dogDetail.temperament}</p>
                        <p>Height:{dogDetail.height}</p>
                        <p>Weight: {dogDetail.weight}</p>
                        <p>Life Span: {dogDetail.life_span}</p>
                    </div>
                </div>
            </div>
        </div>
)

};
