import React from "react"
import { GET_DOGS } from "./actions"

const initialState = {
    dogs: [],
}



export default function rootReducer(state = initialState, action){
    switch(action.type){
        case GET_DOGS: 
        return {
            ...state,
            dogs: action.payload,
            allDogs: action.payload
        }


        default:
            return state


    }}
