import React from 'react'
import { Link, useParams } from 'react-router-dom'
import { useState, useEffect } from 'react'
import { useDispatch, useSelector } from 'react-redux'
import { getDogsById } from '../redux/actions'
import './Detail.css'


export default function Detail(){
const dispatch = useDispatch();
const {id} = useParams()
const [loading, setLoading] = useState(false)
const detail = useSelector(state => state.details)


useEffect( () => {
    setLoading(true)
    setTimeout(() => {
        setLoading(false)
    }, 2000)
    dispatch(getDogsById(id))
}, [dispatch, id])





if(loading){
    return(
        <div>Loading... =P</div>
    )
} else {

} return (
    <div className="details" key= {id}>
        <div className="divimg">
          <img className="detailImg" src= { detail?.image && detail[0].image } alt=""/>  
        </div>
        <h1 className='texts' >{ detail?.name && detail[0].name  }</h1>

        <div> 
            <h3>Temperaments:</h3>
         <ul> 
        { detail[0].temperaments.map(t => <li key={t} >{t}</li>)} 
        </ul>
        </div>

        <div>
            
        </div>


       <Link to="/home" ><button className="backButton">Back</button></Link>
    </div>
)

};
