import React from "react";
import {useState} from 'react';
import {useDispatch/* , useSelector */} from 'react-redux';
import{ getDogsByName, getDogs } from '../redux/actions';
import { Link } from "react-router-dom";
import './SearchBar.css'

export default function SearchBar(){
    const dispatch = useDispatch();
    //const dog = useSelector(state => state.allDogs)
    const [name, setName] = useState("")
    //const [search, setSearch] = useState(false)

  function handleInputChange(e){
    e.preventDefault()
    setName(e.target.value)
    
  } 

  function handleSubmit(e){
    e.preventDefault();
    dispatch(getDogsByName(name))
    if(e.target.value.toLowerCase().includes(!name)){
      alert("That breed does not exist")
     }

    
  }

  function handleBack(e){
    e.preventDefault();
    dispatch(getDogs())
  }


    return(
       <div>
        <input 
        name = 'input'
        type = 'text'
        placeholder = 'Search By Breed...'
        onChange = {(e) => handleInputChange(e)}
        /* id='searchBox' */
        />
        <button className="btn" onClick={(e) => handleSubmit(e)} type = 'submit' >Search</button>
       
      
        <Link to='/home'>
        <button onClick={(e) => handleBack(e)} className="BackBtnSearch">Back</button>
        </Link>
     
       </div>
    )
}
