import React from "react";
import {useState} from 'react';
import {useDispatch , useSelector } from 'react-redux';
import{ getDogsByName, getDogs } from '../redux/actions';
import { Link } from "react-router-dom";
import './SearchBar.css'

export default function SearchBar(){
    const dispatch = useDispatch();
    const dog = useSelector(state => state.allDogs)
    const [name, setName] = useState("")
    const [search, setSearch] = useState(false)

  function handleInputChange(e){
    e.preventDefault()
    setName(e.target.value)
    
  } 

  function handleSubmit(e){
    e.preventDefault();
    
    if(name){
      dispatch(getDogsByName(name))
      setSearch(true) 
      setName("")
    }else{
      setSearch(false)
      return alert("Search an existent breed")
    }
    if(!dog.name.toLowerCase()){
      setSearch(false)
      return alert("Please choose an existent breed")
    }

    
  }

  function handleBack(e){
    e.preventDefault();
    dispatch(getDogs())
    setName(e.target.value = "")
  }


    return(
       <div>
        <input 
        name = 'input'
        type = 'text'
        placeholder = 'Search By Breed...'
        onChange = {(e) => handleInputChange(e)}
        /* id='searchBox' */
        />
        <button className="btn" onClick={(e) => handleSubmit(e)} type = 'submit' >Search</button>
       
      
        <Link to='/home'>
        <button onClick={(e) => handleBack(e)} className="BackBtnSearch">Back</button>
        </Link>
     
       </div>
    )
}
