import React from "react";
import { useState, useEffect } from 'react'
import { useSelector, useDispatch } from 'react-redux'
import { Link, useHistory } from 'react-router-dom'
import { postDog, getTemperaments } from "../redux/actions";

export default function Form(){

    const dispatch = useDispatch();
    const temperament = useSelector(state => state.temperaments);
    const history = useHistory(); 
  

    const [input, setInput] = useState({
        image:"",
        name:"",
        temperaments:[],
        life_span: "",
        weight_max: "",
        weight_min: "",
        height_max: "",
        height_min: ""

    })

    useEffect(() => {
        dispatch(getTemperaments())
    },[dispatch])



    function handleSubmit(e){
     e.preventDefault();
     dispatch(postDog(input))
     alert('New breed has been created')
     setInput({
        /* image:"", */
        name:"",
        temperaments:[],
        life_span: "",
        weight_max: "",
        weight_min: "",
        height_max: "",
        height_min: ""
     })
     history.push('/home')
    }


    function handleChange(e){
      e.preventDefault();
      setInput((prevInput) => {
        const newInput = {
            ...prevInput,
            [e.target.name] : e.target.value
        }
        return newInput
      })
    }

    function handleSelect(e){
      e.preventDefault();
      setInput({
            ...input,
            temperaments: [...input.temperaments, e.target.value]
           }
        )

    }



    return (
        <div>
            <h2>Create your breeeeeed</h2>
            <form onSubmit={(e) => handleSubmit(e)} >
                <div>
                   
                    <div>
                        <label>
                            Name:
                        </label>
                        <input
                        type="text"
                        value={input.name}
                        name='name'
                        onChange={e => handleChange(e)}
                        />
                    </div>
                    <div>
{
                    <select  onChange={handleSelect}>
                            <option disabled selected>Temperaments</option>
                            {temperament.map(d => (                    
                                <option value={d.name} key={d.name+Math.random()} >{d.name}</option> //key de elementos de temperamentos, eliminar el repetido reserved
                            ))}
                        </select> }

                        {/* <select>
                            {temperaments.map((temp) => (
                                <option value={temp.name}> {temp.name} </option>
                            ))}
                        </select> */}
                        {/* <label>
                          Temperaments:
                        </label>
                        <input
                        type="select"
                        value={temperaments}
                        name={temperaments}
                        onChange={e => handleChange(e)}
                        /> */}
                    </div>
                    <div>
                        <label>
                            Weight_max:
                        </label>
                        <input
                        type="number"
                        value={input.weight_max}
                        name="weight_max"
                        onChange={e => handleChange(e)}
                        />
                    </div>
                    <div>
                        <label>
                            Weight_min:
                        </label>
                        <input
                        type="number"
                        value={input.weight_min}
                        name="weight_min"
                        onChange={e => handleChange(e)}
                        />
                    </div>
                    <div>
                        <label>
                            Height_max:
                        </label>
                        <input
                        type="number"
                        value={input.height_max}
                        name="height_max"
                        onChange={e => handleChange(e)}
                        />
                    </div>
                    <div>
                        <label>
                            Height_min:
                        </label>
                        <input
                        type="number"
                        value={input.height_min}
                        name="height_min"
                        onChange={e => handleChange(e)}
                        />
                    </div>
                    <div>
                        <label>
                            Life Span:
                        </label>
                        <input
                        type="text"
                        value={input.life_span}
                        name="life_span"
                        onChange={e => handleChange(e)}
                        />
                    </div>
                </div>
                <div>
                    <button type="submit">Submit</button>
                </div>

                <div>
                <Link to='/home'>
                    <button className="backBtn">Back</button>
                </Link>
                </div>
            </form>
        </div>
    )
};
