import React, {useState} from "react";
import './Paginado.css'



export default function Paginado({dogsPerPage, allDogs, paginado}){
    const pageNumber = [];


    const [currentPage, setCurrentPage] = useState(1)

const indexOfLast = currentPage * dogsPerPage
//const indexOfFirst = indexOfLast - dogsPerPage
const [currentDogs, setCurrentDogs] = useState([])



    for(let i = 1; i <= Math.ceil(allDogs/dogsPerPage)-1; i++){
        pageNumber.push(i)
    }




    function handlePrevClick(){
        if(currentPage > 1){
       setCurrentDogs(currentDogs - dogsPerPage)
       setCurrentPage(currentPage - 1)
        }
    }

    function handleNextClick(){
        if(currentPage < indexOfLast){
            setCurrentDogs(currentDogs + dogsPerPage)
            setCurrentPage(currentPage + 1)
             }
    }




    return(
        <div>
            <nav className="pagination">
                <ul className="pages">
                <li>
                   <button onClick={handlePrevClick} disabled={dogsPerPage === pageNumber[0]}>Prev</button>
               </li>
                    {
                        pageNumber && pageNumber.map(number => (
                            <li key={number}>
                               <button className="PageBtn" onClick={() => paginado(number)}>{number}</button>
                            </li>
                        ))
                    }
                     <li>
                   <button onClick={handleNextClick} disabled={dogsPerPage === pageNumber[pageNumber.length-1]}>Next</button>
               </li>
                </ul>
            </nav>
        </div>
    )
}
